const getBoolOption = (key, value, defaultValue = false) => {
  if (typeof value === 'undefined') {
    return defaultValue;
  }

  if (typeof value !== 'boolean') {
    throw new Error(
      `[babel-preset-febs]: option '${key}' must be a boolean value`
    );
  }

  return value;
};

module.exports = {
  getBoolOption,
};
