declare module "@sqs/cookie-cutter" {
  interface CookieOptions {
    expires?: string;
    path?: string;
    domain?: string;
    secure?: boolean;
  }

  // This interface represents the instance methods available on a Cookie object
  interface CookieInstance {
    get(key: string): string | undefined;
    set(key: string, value: string, opts?: CookieOptions): void;
  }

  // Defining a constructor type that can be invoked to create a new instance
  interface CookieConstructor extends CookieInstance {
    new (document: Document, secure: boolean): CookieInstance;
    (document: Document, secure: boolean): CookieInstance;
  }

  // Declare the exported variable which acts like both a function and an object
  const Cookie: CookieConstructor;

  export default Cookie;
}
