export const timesTwo = (num: number) => num *2

export const sayHello = (name:string) => `${HELLO  }, ${  name}!`;

// @ts-ignore
const findMapped = <T, R>(
    array: T[] | null | undefined,
    fn: <D>(ar: any) => R | undefined
  ): R | undefined => { return fn(array) };

const zipWithIndex = function <T>(a:T[]) : T[] { return a; };

export const findMappedWithIndex = <T, R>(arr: T[], mapped: (item: T) => R): [number, R] | undefined =>
findMapped(zipWithIndex(arr), (pair) => {
    const [idx, item] = pair;
    const found = mapped(item);
    if (found) {
    return [idx, found];
    }
})
