const RuleTestRunner = require('../rule-test-runner');

const runner = new RuleTestRunner();

runner.run('i18n-jsx-no-nested-elements', {
  valid: [
    'const myComp = <div><T>Testing My Component</T></div>',
    "const myComp = <div><T>{'Testing My {component}'}</T></div>",
    'const myComp = <div>Testing My Component</div>',
    'const myComp = <div><div>Nested without </div><div>gaps</div></div>',
  ],
  invalid: [
    {
      code: 'const myComp = <div><T><span>My component</span></T></div>',
      errors: [
        {
          message: 'No nested JSXElements are permitted within the T component',
          type: 'JSXElement',
          line: 1,
          column: 24,
        },
      ],
    },
    {
      code: 'const myComp = <div>This is <div><T>my <span>really good</span> component</T></div></div>',
      errors: [
        {
          message: 'No nested JSXElements are permitted within the T component',
          type: 'JSXElement',
          line: 1,
          column: 40,
        },
      ],
    },
  ],
});