const RuleTestRunner = require('../rule-test-runner');

const runner = new RuleTestRunner();

runner.run('i18n-no-missing-notes', {
  valid: [
    '<T project="my.project" notes="This is the name of a town in Texas">Hellodis</T>',
    "t('Hellodis.', null, { project: 'my.project', notes: 'This is the name of a town in Texas' })",
    "const myNonTMethod = hello([{ path, element }])",
    "t('Hello {name}', { name: 'Stephanie'} , {project: 'yo', notes: 'sup' });"
  ],
  invalid: [
    {
      code: '<T project="my.project" notes="">Hellodis</T>',
      errors: [
        {
          messageId: 'missingTranslationNotes',
          type: 'JSXElement',
        },
      ],
    },
    {
      code:
        "t('Hellodis', null, {project: 'my.project', notes: ''})",
      errors: [
        {
          messageId: 'missingTranslationNotes',
          type: 'ObjectExpression',
        },
      ],
    },
    {
      code: '<T project="my.project">Hellodis</T>',
      errors: [
        {
          messageId: "missingTranslationNotes",
          type: 'JSXElement',
        },
      ],
    },
    {
      code:
        "t('Hellodis', null, {project: 'my.project'})",
      errors: [
        {
          messageId: "missingTranslationNotes",
          type: 'ObjectExpression',
        },
      ],
    },
    {
      code:
        "t('Hello {name}', { name: 'Stephanie'} , {project: 'yo'})",
      errors: [
        {
          messageId: "missingTranslationNotes",
          type: 'ObjectExpression',
        },
      ],
    },
  ],
});
