/**
 * @fileoverview Prefer use of the apostrophe symbol when using contractions or possessives in string literals.
 * @author Stephanie Manwaring
 */

const RuleTestRunner = require('../rule-test-runner');

const runner = new RuleTestRunner();

runner.run('no-locale-in-help-center-article-link', {
  valid: [
    "'support.squarespace.com/hc/articles/boop'",
    "'/api/auth/sso/help?path=/hc/articles/123'",
  ],
  invalid: [
    {
      code: "'support.squarespace.com/hc/es-419/articles/boop'",
      errors: [
        {
          message: 'Help Center link should not specify a locale in the URL.',
          type: 'Literal',
        },
      ],
    },
    {
      code: "'/api/auth/sso/help?path=/hc/123/articles/123'",
      errors: [
        {
          message: 'Help Center link should not specify a locale in the URL.',
          type: 'Literal',
        },
      ],
    },
  ],
});
