/**
 * @fileoverview Prefer use of the apostrophe symbol when using contractions or possessives in string literals.
 * @author Stephanie Manwaring
 */

const RuleTestRunner = require('../rule-test-runner');

const runner = new RuleTestRunner();

runner.run('prefer-apostrophe-in-string-literal', {
  valid: [
    'throw new Error("The cat\'s pajamas")',
    "describe(\"The cat's pajamas are so 'cool'\")",
    "it(\"The cat's pajamas are so 'cool'\")",
    'myFunc("\'abcd \'abcd")',
    'throw "ab\'ab"',
  ],
  invalid: [
    {
      code: 'myFunc("The cat\'s pajamas")',
      errors: [
        {
          message:
            'Replace single quote with apostrophe symbol ’ in contractions and possessives.',
          type: 'Literal',
        },
      ],
      output: "myFunc('The cat’s pajamas')",
    },
  ],
});
