const { getPackageNameWithoutSqsScope } = require('../utils');

describe('Utils', () => {
  it('Trims @sqs scope from package name', () => {
    expect(getPackageNameWithoutSqsScope('@sqs/foo')).toEqual('foo');
  });

  it('Leaves other @scope alone', () => {
    expect(getPackageNameWithoutSqsScope('@scope/foo')).toEqual('@scope/foo');
  });

  it('Leaves other unscoped names alone', () => {
    expect(getPackageNameWithoutSqsScope('foo')).toEqual('foo');
  });
});