package com.squarespace.reactnative.toolkit;

import java.util.HashMap;
import java.util.Map;
import java.util.UUID;

/**
 * A cache for retrieving reactViews.
 * Should be application scoped
 */
public class ReactViewRegistry {

  private Map<UUID, BaseReactView> reactViews = new HashMap<>();

  public void registerView(UUID uuid, BaseReactView baseReactView) {
    reactViews.put(uuid, baseReactView);
  }

  public void unregisterView(UUID uuid) {
    reactViews.remove(uuid);
  }

  public BaseReactView getView(UUID uuid) {
    return reactViews.get(uuid);
  }
}
