package com.squarespace.reactnative.toolkit.app.delegate

import com.facebook.react.ReactInstanceManager
import com.squarespace.android.commons.app.DelegateActivity

class ReactNativeActivityDelegates {
  companion object {
    @JvmStatic
    fun attach(delegateActivity: DelegateActivity, reactInstanceManager: ReactInstanceManager) {
      delegateActivity.apply {

        ReactNativeLifecycleDelegate(this, reactInstanceManager)
            .let { lifeCycleDelegates.add(it) }

        ReactNativeActivityResultDelegate(this, reactInstanceManager)
            .let { activityResultDelegates.add(it) }

        ReactNativeKeyPressDelegate(reactInstanceManager)
            .let { keyPressDelegates.add(it) }
      }
    }
  }
}