package com.squarespace.reactnative.toolkit.app.delegate

import android.app.Activity
import android.content.Intent
import com.facebook.react.ReactInstanceManager
import com.squarespace.android.commons.app.delegate.ActivityResultDelegate

class ReactNativeActivityResultDelegate(
  private val activity: Activity,
  private val reactInstanceManager: ReactInstanceManager?
) : ActivityResultDelegate {

  override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {
    reactInstanceManager?.onActivityResult(activity, requestCode, resultCode, data)
  }

}