package com.squarespace.reactnative.toolkit.app.delegate

import android.view.KeyEvent
import com.facebook.react.ReactInstanceManager
import com.squarespace.android.commons.app.delegate.KeyPressDelegate

class ReactNativeKeyPressDelegate(private val reactInstanceManager: ReactInstanceManager?) : KeyPressDelegate {

  override fun onKeyUp(keyCode: Int, event: KeyEvent?): Boolean {
    if (KeyEvent.KEYCODE_MENU == keyCode) {
      reactInstanceManager?.showDevOptionsDialog()
      return true
    }
    return false
  }

}