package com.squarespace.reactnative.toolkit.arguments

import com.facebook.react.bridge.ReadableArray
import com.facebook.react.bridge.ReadableMap
import com.google.gson.JsonArray
import com.google.gson.JsonObject

/**
 * @see ArgumentUtils.fromJsonObject
 */
fun JsonObject.asWritableMap() = ArgumentUtils.fromJsonObject(this)

/**
 * @see ArgumentUtils.fromJsonArray
 */
fun JsonArray.asWritableArray() = ArgumentUtils.fromJsonArray(this)

/**
 * @see ArgumentUtils.toJsonObject
 */
fun ReadableMap.asJsonObject() = ArgumentUtils.toJsonObject(this)

/**
 * @see ArgumentUtils.toJsonArray
 */
fun ReadableArray.asJsonArray() = ArgumentUtils.toJsonArray(this)