package com.squarespace.reactnative.toolkit.context

import androidx.annotation.MainThread
import com.facebook.react.bridge.ReactContext

/**
 * Allows late initialization of [ReactContext] by performing work the context whenever it's available.
 */
interface ReactContextProvider {
  /**
   * Perform an [action] whenever the [ReactContext] is available.
   *
   * Must be run on the main thread.
   */
  @MainThread
  fun withContext(action: (ReactContext) -> Unit)
}
