package com.squarespace.reactnative.toolkit.lifecycle

import com.facebook.react.bridge.LifecycleEventListener

/**
 * Convenience [LifecycleEventListener] for when you only want to listen to
 * [LifecycleEventListener.onHostDestroy].
 */
interface OnDestroyListener : LifecycleEventListener {
  override fun onHostPause() { /* no op */ }
  override fun onHostResume() { /* no op */ }
}
