import cookie from '@sqs/cookie-cutter';
import { getSessionInfo } from '../index';

jest.mock('@sqs/cookie-cutter');

describe('SessionInfo', () => {
  afterEach(() => {
    jest.clearAllMocks();
  });

  it('Sets cookie by default', () => {
    getSessionInfo();
    expect(cookie.set).toHaveBeenCalled();
  });

  it('Should not set cookie if not enabled', () => {
    getSessionInfo(false);
    expect(cookie.set).not.toHaveBeenCalled();
  });
});