package com.squarespace.reactnative.network

import okhttp3.ResponseBody
import retrofit2.Response
import java.lang.Exception

/**
 * Throw when expected failures occur within the [NetworkBridge]
 */
class NetworkBridgeException(message: String) : Exception(message) {

  constructor(response: Response<ResponseBody>)
    : this("Unsuccessful response (${response.code()}) - ${response.errorBody()?.string()}")
}
