package com.squarespace.reactnative.network.client

import com.squarespace.android.api.dynamic.DynamicClient

/**
 * Construct network clients.
 */
interface ClientFactory {
  /**
   * Creates an [DynamicClient.IdentifiedRequest] that can be used to make an (a)sync network request.
   *
   * It is recommended that you call this on a background thread as there is no guarantee
   * that this will immediately return.
   */
  fun create(): DynamicClient.IdentifiedRequest
}
