/**
 * This util is a self executing closure that caches the value of `Static.SQUARESPACE_CONTEXT`
 * so any user modifications after initial page load are ignored.
 *
 * Do not use on `config/` pages. This context value is bound to visitor pages.
 */
export var context = (() => {
  try {
    return window.Static.SQUARESPACE_CONTEXT;
  } catch (e) {
    throw new Error('@sqs/website-context was imported before window.Static.SQUARESPACE_CONTEXT was set');
  }
})();